Ext.data.JsonP['Reviews']({"tagname":"class","name":"Reviews","alternateClassNames":[],"mixins":["Reviewable"],"meta":{},"aliases":{},"files":[{"filename":"query.yml","href":"/var/lib/jenkins/jobs/appc-docs/workspace/cloud_docs/apidoc/Reviews/query.yml"},{"filename":"batch_delete.yml","href":"/var/lib/jenkins/jobs/appc-docs/workspace/cloud_docs/apidoc/Reviews/batch_delete.yml"},{"filename":"show.yml","href":"/var/lib/jenkins/jobs/appc-docs/workspace/cloud_docs/apidoc/Reviews/show.yml"},{"filename":"count.yml","href":"/var/lib/jenkins/jobs/appc-docs/workspace/cloud_docs/apidoc/Reviews/count.yml"},{"filename":"create.yml","href":"/var/lib/jenkins/jobs/appc-docs/workspace/cloud_docs/apidoc/Reviews/create.yml"},{"filename":"update.yml","href":"/var/lib/jenkins/jobs/appc-docs/workspace/cloud_docs/apidoc/Reviews/update.yml"},{"filename":"Reviews.yml","href":"/var/lib/jenkins/jobs/appc-docs/workspace/cloud_docs/apidoc/Reviews/Reviews.yml"},{"filename":"delete.yml","href":"/var/lib/jenkins/jobs/appc-docs/workspace/cloud_docs/apidoc/Reviews/delete.yml"}],"members":{"cfg":[],"property":[{"name":"checkin","tagname":"property","owner":"Reviews","meta":{"[:hide]":false},"id":"property-checkin"},{"name":"content","tagname":"property","owner":"Reviews","meta":{"[:hide]":false},"id":"property-content"},{"name":"created_at","tagname":"property","owner":"Reviews","meta":{"[:hide]":false},"id":"property-created_at"},{"name":"custom_fields","tagname":"property","owner":"Reviews","meta":{"[:hide]":false},"id":"property-custom_fields"},{"name":"custom_object","tagname":"property","owner":"Reviews","meta":{"[:hide]":false},"id":"property-custom_object"},{"name":"event","tagname":"property","owner":"Reviews","meta":{"[:hide]":false},"id":"property-event"},{"name":"photo","tagname":"property","owner":"Reviews","meta":{"[:hide]":false},"id":"property-photo"},{"name":"place","tagname":"property","owner":"Reviews","meta":{"[:hide]":false},"id":"property-place"},{"name":"post","tagname":"property","owner":"Reviews","meta":{"[:hide]":false},"id":"property-post"},{"name":"rating","tagname":"property","owner":"Reviews","meta":{"[:hide]":false},"id":"property-rating"},{"name":"ratings_average","tagname":"property","owner":"Reviewable","meta":{"[:hide]":false},"id":"property-ratings_average"},{"name":"ratings_count","tagname":"property","owner":"Reviewable","meta":{"[:hide]":false},"id":"property-ratings_count"},{"name":"ratings_summary","tagname":"property","owner":"Reviewable","meta":{"[:hide]":false},"id":"property-ratings_summary"},{"name":"review","tagname":"property","owner":"Reviews","meta":{"[:hide]":false},"id":"property-review"},{"name":"reviewed_object","tagname":"property","owner":"Reviews","meta":{"[:hide]":false},"id":"property-reviewed_object"},{"name":"reviews","tagname":"property","owner":"Reviewable","meta":{"[:hide]":false},"id":"property-reviews"},{"name":"reviews_count","tagname":"property","owner":"Reviewable","meta":{"[:hide]":false},"id":"property-reviews_count"},{"name":"status","tagname":"property","owner":"Reviews","meta":{"[:hide]":false},"id":"property-status"},{"name":"tags","tagname":"property","owner":"Reviews","meta":{"[:hide]":false},"id":"property-tags"},{"name":"updated_at","tagname":"property","owner":"Reviews","meta":{"[:hide]":false},"id":"property-updated_at"},{"name":"user","tagname":"property","owner":"Reviews","meta":{"[:hide]":false},"id":"property-user"}],"method":[{"name":"reviews/batch_delete.json","tagname":"method","owner":"Reviews","meta":{"hide":false,"loginRequired":true,"adminRequired":true},"id":"method-batch_delete"},{"name":"reviews/count.json","tagname":"method","owner":"Reviews","meta":{"hide":false,"loginRequired":false,"adminRequired":false},"id":"method-count"},{"name":"reviews/create.json","tagname":"method","owner":"Reviews","meta":{"hide":false,"loginRequired":true,"adminRequired":false},"id":"method-create"},{"name":"reviews/delete.json","tagname":"method","owner":"Reviews","meta":{"hide":false,"loginRequired":true,"adminRequired":false},"id":"method-delete"},{"name":"reviews/query.json","tagname":"method","owner":"Reviews","meta":{"hide":false,"loginRequired":false,"adminRequired":false},"id":"method-query"},{"name":"reviews/show.json","tagname":"method","owner":"Reviews","meta":{"hide":false,"loginRequired":false,"adminRequired":false},"id":"method-show"},{"name":"reviews/update.json","tagname":"method","owner":"Reviews","meta":{"hide":false,"loginRequired":true,"adminRequired":false},"id":"method-update"}],"event":[],"css_var":[],"css_mixin":[]},"extends":null,"singleton":null,"private":null,"html_meta":{},"statics":{"cfg":[],"property":[],"method":[],"event":[],"css_var":[],"css_mixin":[]},"component":false,"superclasses":[],"subclasses":[],"mixedInto":[],"parentMixins":[],"requires":[],"uses":[],"html":"<div><div class='sidebar'></div><div class='hierarchy'></div><div class='doc-contents'><p>The Reviews API lets you associate user reviews and ratings with an ArrowDB object. Currently, reviews\ncan be added to the following ArrowDB object types:</p>\n\n<ul>\n<li><a href=\"#!/api/Posts\" rel=\"Posts\" class=\"docClass\">Posts</a></li>\n<li><a href=\"#!/api/Photos\" rel=\"Photos\" class=\"docClass\">Photos</a></li>\n<li><a href=\"#!/api/Users\" rel=\"Users\" class=\"docClass\">Users</a></li>\n<li><a href=\"#!/api/Events\" rel=\"Events\" class=\"docClass\">Events</a></li>\n<li><a href=\"#!/api/Checkins\" rel=\"Checkins\" class=\"docClass\">Checkins</a></li>\n<li><a href=\"#!/api/Places\" rel=\"Places\" class=\"docClass\">Places</a></li>\n<li><a href=\"#!/api/CustomObjects\" rel=\"CustomObjects\" class=\"docClass\">CustomObjects</a></li>\n<li><a href=\"#!/api/Statuses\" rel=\"Statuses\" class=\"docClass\">Statuses</a></li>\n<li><a href=\"#!/api/Reviews\" rel=\"Reviews\" class=\"docClass\">Reviews</a></li>\n</ul>\n\n\n<p>If an ArrowDB object has one or more reviews associated with it, it returns the total\nnumber of reviews, number of ratings, average rating value, and a summary of ratings when the object is\nqueried or updated:</p>\n\n<pre><code>\"reviews_count\": 2,\n\"ratings_count\": 2,\n\"ratings_average\": 150.0,\n\"ratings_summary\": {\n      \"100\": 1,\n        \"200\": 1\n}\n</code></pre>\n</div><div class='members'><div class='members-section'><div class='definedBy'>Defined By</div><h3 class='members-title icon-property'>Fields</h3><div class='subsection'><div id='property-checkin' class='member first-child not-inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><span class='defined-in' rel='Reviews'>Reviews</span></div><a href='#!/api/Reviews-property-checkin' class='name not-expandable'>checkin</a><span> : <a href=\"#!/api/Checkins\" rel=\"Checkins\" class=\"docClass\">Checkins</a></span></div><div class='description'><div class='short'><p>Checkin object associated with this review.</p>\n</div><div class='long'><p>Checkin object associated with this review.</p>\n</div></div></div><div id='property-content' class='member  not-inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><span class='defined-in' rel='Reviews'>Reviews</span></div><a href='#!/api/Reviews-property-content' class='name not-expandable'>content</a><span> : String</span></div><div class='description'><div class='short'><p>Review or comment text.</p>\n</div><div class='long'><p>Review or comment text.</p>\n</div></div></div><div id='property-created_at' class='member  not-inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><span class='defined-in' rel='Reviews'>Reviews</span></div><a href='#!/api/Reviews-property-created_at' class='name not-expandable'>created_at</a><span> : Date</span></div><div class='description'><div class='short'><p>Creation date for this review object.</p>\n</div><div class='long'><p>Creation date for this review object.</p>\n</div></div></div><div id='property-custom_fields' class='member  not-inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><span class='defined-in' rel='Reviews'>Reviews</span></div><a href='#!/api/Reviews-property-custom_fields' class='name expandable'>custom_fields</a><span> : String/Hash</span></div><div class='description'><div class='short'>User defined fields. ...</div><div class='long'><p>User defined fields. See <a href=\"#!/guide/customfields\">Custom Data Fields</a>.</p>\n</div></div></div><div id='property-custom_object' class='member  not-inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><span class='defined-in' rel='Reviews'>Reviews</span></div><a href='#!/api/Reviews-property-custom_object' class='name not-expandable'>custom_object</a><span> : <a href=\"#!/api/CustomObjects\" rel=\"CustomObjects\" class=\"docClass\">CustomObjects</a></span></div><div class='description'><div class='short'><p>Custom object associated with this review.</p>\n</div><div class='long'><p>Custom object associated with this review.</p>\n</div></div></div><div id='property-event' class='member  not-inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><span class='defined-in' rel='Reviews'>Reviews</span></div><a href='#!/api/Reviews-property-event' class='name not-expandable'>event</a><span> : <a href=\"#!/api/Events\" rel=\"Events\" class=\"docClass\">Events</a></span></div><div class='description'><div class='short'><p>Event object associated with this review.</p>\n</div><div class='long'><p>Event object associated with this review.</p>\n</div></div></div><div id='property-photo' class='member  not-inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><span class='defined-in' rel='Reviews'>Reviews</span></div><a href='#!/api/Reviews-property-photo' class='name not-expandable'>photo</a><span> : <a href=\"#!/api/Photos\" rel=\"Photos\" class=\"docClass\">Photos</a></span></div><div class='description'><div class='short'><p>Photo object associated with this review.</p>\n</div><div class='long'><p>Photo object associated with this review.</p>\n</div></div></div><div id='property-place' class='member  not-inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><span class='defined-in' rel='Reviews'>Reviews</span></div><a href='#!/api/Reviews-property-place' class='name not-expandable'>place</a><span> : <a href=\"#!/api/Places\" rel=\"Places\" class=\"docClass\">Places</a></span></div><div class='description'><div class='short'><p>Place object associated with this review.</p>\n</div><div class='long'><p>Place object associated with this review.</p>\n</div></div></div><div id='property-post' class='member  not-inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><span class='defined-in' rel='Reviews'>Reviews</span></div><a href='#!/api/Reviews-property-post' class='name not-expandable'>post</a><span> : <a href=\"#!/api/Posts\" rel=\"Posts\" class=\"docClass\">Posts</a></span></div><div class='description'><div class='short'><p>Post object associated with this review.</p>\n</div><div class='long'><p>Post object associated with this review.</p>\n</div></div></div><div id='property-rating' class='member  not-inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><span class='defined-in' rel='Reviews'>Reviews</span></div><a href='#!/api/Reviews-property-rating' class='name not-expandable'>rating</a><span> : String</span></div><div class='description'><div class='short'><p>Rating associated with review.</p>\n</div><div class='long'><p>Rating associated with review.</p>\n</div></div></div><div id='property-ratings_average' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/Reviewable' rel='Reviewable' class='defined-in docClass'>Reviewable</a></div><a href='#!/api/Reviewable-property-ratings_average' class='name expandable'>ratings_average</a><span> : Number</span></div><div class='description'><div class='short'>Average rating for this object. ...</div><div class='long'><p>Average rating for this object.\nOnly present if the object has been reviewed.</p>\n</div></div></div><div id='property-ratings_count' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/Reviewable' rel='Reviewable' class='defined-in docClass'>Reviewable</a></div><a href='#!/api/Reviewable-property-ratings_count' class='name expandable'>ratings_count</a><span> : Number</span></div><div class='description'><div class='short'>Total number of reviews for this object that include a rating. ...</div><div class='long'><p>Total number of reviews for this object that include a rating.</p>\n\n<p>Only present if the object has been reviewed.</p>\n</div></div></div><div id='property-ratings_summary' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/Reviewable' rel='Reviewable' class='defined-in docClass'>Reviewable</a></div><a href='#!/api/Reviewable-property-ratings_summary' class='name expandable'>ratings_summary</a><span> : Hash</span></div><div class='description'><div class='short'>Breakdown of the number of reviews that specified a given rating value. ...</div><div class='long'><p>Breakdown of the number of reviews that specified a given rating value. For\nexample, if your ratings range from 1-5, the ratings summary might look like this:</p>\n\n<pre><code>ratings_summary: {\n    \"1\" : 1,\n    \"2\" : 0,\n    \"3\" : 5,\n    \"4\" : 50,\n    \"5\" : 12\n}\n</code></pre>\n\n<p>Only present if the object has been reviewed.</p>\n</div></div></div><div id='property-review' class='member  not-inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><span class='defined-in' rel='Reviews'>Reviews</span></div><a href='#!/api/Reviews-property-review' class='name not-expandable'>review</a><span> : <a href=\"#!/api/Reviews\" rel=\"Reviews\" class=\"docClass\">Reviews</a></span></div><div class='description'><div class='short'><p>Review object associated with this review.</p>\n</div><div class='long'><p>Review object associated with this review.</p>\n</div></div></div><div id='property-reviewed_object' class='member  not-inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><span class='defined-in' rel='Reviews'>Reviews</span></div><a href='#!/api/Reviews-property-reviewed_object' class='name expandable'>reviewed_object</a><span> : Hash</span></div><div class='description'><div class='short'>Object whose type and id fields identify, respectively, the type (Status or Photo, for example) and\nthe ID of the rev...</div><div class='long'><p>Object whose <code>type</code> and <code>id</code> fields identify, respectively, the type (Status or Photo, for example) and\nthe ID of the reviewed object.</p>\n</div></div></div><div id='property-reviews' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/Reviewable' rel='Reviewable' class='defined-in docClass'>Reviewable</a></div><a href='#!/api/Reviewable-property-reviews' class='name expandable'>reviews</a><span> : <a href=\"#!/api/Reviews\" rel=\"Reviews\" class=\"docClass\">Reviews</a>[]</span></div><div class='description'><div class='short'>List of reviews for this object. ...</div><div class='long'><p>List of reviews for this object.</p>\n\n<p>Only present if the object has been reviewed.</p>\n</div></div></div><div id='property-reviews_count' class='member  inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><a href='#!/api/Reviewable' rel='Reviewable' class='defined-in docClass'>Reviewable</a></div><a href='#!/api/Reviewable-property-reviews_count' class='name expandable'>reviews_count</a><span> : Number</span></div><div class='description'><div class='short'>Total number of reviews for this object. ...</div><div class='long'><p>Total number of reviews for this object.</p>\n\n<p>Only present if the object has been reviewed.</p>\n</div></div></div><div id='property-status' class='member  not-inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><span class='defined-in' rel='Reviews'>Reviews</span></div><a href='#!/api/Reviews-property-status' class='name not-expandable'>status</a><span> : <a href=\"#!/api/Statuses\" rel=\"Statuses\" class=\"docClass\">Statuses</a></span></div><div class='description'><div class='short'><p>Status object associated with this review.</p>\n</div><div class='long'><p>Status object associated with this review.</p>\n</div></div></div><div id='property-tags' class='member  not-inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><span class='defined-in' rel='Reviews'>Reviews</span></div><a href='#!/api/Reviews-property-tags' class='name not-expandable'>tags</a><span> : String[]</span></div><div class='description'><div class='short'><p>List of tags for this review.</p>\n</div><div class='long'><p>List of tags for this review.</p>\n</div></div></div><div id='property-updated_at' class='member  not-inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><span class='defined-in' rel='Reviews'>Reviews</span></div><a href='#!/api/Reviews-property-updated_at' class='name not-expandable'>updated_at</a><span> : Date</span></div><div class='description'><div class='short'><p>Last update time for this review object.</p>\n</div><div class='long'><p>Last update time for this review object.</p>\n</div></div></div><div id='property-user' class='member  not-inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><span class='defined-in' rel='Reviews'>Reviews</span></div><a href='#!/api/Reviews-property-user' class='name not-expandable'>user</a><span> : <a href=\"#!/api/Users\" rel=\"Users\" class=\"docClass\">Users</a></span></div><div class='description'><div class='short'><p>User who created the review.</p>\n</div><div class='long'><p>User who created the review.</p>\n</div></div></div></div></div><div class='members-section'><div class='definedBy'>Defined By</div><h3 class='members-title icon-method'>Methods</h3><div class='subsection'><div id='method-batch_delete' class='member first-child not-inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><span class='defined-in' rel='Reviews'>Reviews</span></div><strong class='http-method'>DELETE </strong><a href='#!/api/Reviews-method-batch_delete' class='name expandable'>reviews/batch_delete.json</a><strong class='signature'>admin-only</strong></div><div class='description'><div class='short'>Deletes Reviews objects that match the query constraints provided in the where parameter. ...</div><div class='long'><p>Deletes Reviews objects that match the query constraints provided in the <code>where</code> parameter.\nIf no <code>where</code> parameter is provided, all Reviews objects are deleted.\nNote that an HTTP 200 code (success)\nis returned if the call completed successfully but the query matched no objects.</p>\n\n<p>For performance reasons, the number of objects that can be deleted in a single batch delete\noperation is limited to 100,000.</p>\n\n<p>The matched objects are deleted asynchronously in a separate process.</p>\n\n<p>The reviewed object (<a href=\"#!/api/Reviews-property-post\" rel=\"Reviews-property-post\" class=\"docClass\">Post</a>, <a href=\"#!/api/Reviews-property-photo\" rel=\"Reviews-property-photo\" class=\"docClass\">Photo</a>, <a href=\"#!/api/Reviews-property-user\" rel=\"Reviews-property-user\" class=\"docClass\">User</a>, <a href=\"#!/api/Reviews-property-event\" rel=\"Reviews-property-event\" class=\"docClass\">Event</a>,\n<a href=\"#!/api/Reviews-property-checkin\" rel=\"Reviews-property-checkin\" class=\"docClass\">Checkin</a>, <a href=\"#!/api/Reviews-property-place\" rel=\"Reviews-property-place\" class=\"docClass\">Place</a>, <a href=\"#!/api/Reviews-property-custom_object\" rel=\"Reviews-property-custom_object\" class=\"docClass\">CustomObject</a>,\n<a href=\"#!/api/Reviews-property-status\" rel=\"Reviews-property-status\" class=\"docClass\">Status</a>, or <a href=\"#!/api/Reviews-property-review\" rel=\"Reviews-property-review\" class=\"docClass\">Review</a>) of each matched object is not deleted.</p>\n\n<p>You must be an application admin to run this command.</p>\n<strong>User Login Required: Yes</strong><h3 class=\"pa\">Parameters</h3><ul><li><span class='pre'>where</span> : Hash<div class='sub-desc'><p>Encoded JSON object that specifies constraint values for Reviews objects to delete.\nIf not specified, all Reviews objects are deleted.</p>\n</div></li></ul><div class='examples-section'><h3 class='examples-title icon-examples'>Examples</h3><div class='example example-titanium' platform='titanium' platform_name='Titanium SDK'><p>Example request to delete all Review objects.</p>\n\n<pre><code>Cloud.sendRequest({\n    url : \"reviews/batch_delete.json\",\n    method : \"DELETE\"\n}, function(e) {\n    if (e.success) {\n        alert(\"All Review objects have been deleted.\");\n    } else {\n        alert('Error:\\n' + ((e.error &amp;&amp; e.message) || JSON.stringify(e)));\n    }\n});\n</code></pre>\n</div><div class='example example-rest' platform='rest' platform_name='REST API'><p>Example cURL request:</p>\n\n<pre><code>$ curl -b cookies.txt -c cookies.txt -X DELETE -F \"where={\\\"favorite_color\\\":\\\"blue\\\"}\" https://api.cloud.appcelerator.com/v1/reviews/batch_delete.json?key=&lt;API_KEY&gt;&amp;pretty_json=true                 \n</code></pre>\n\n<p>Example JSON response:</p>\n\n<pre><code>{\n  \"meta\": {\n    \"status\": \"ok\",\n    \"code\": 200,\n    \"method_name\": \"batchDelete\"\n  }\n}\n</code></pre>\n</div><div class='example example-ios' platform='ios' platform_name='iOS SDK'><p>Example request to delete all Review objects.</p>\n\n<pre><code>[[APSCloud sharedInstance] sendRequest:@\"reviews/batch_delete.json\" method:@\"DELETE\" data:nil handler:^(APSResponse *e){\n    if (e.success) {\n        [[[UIAlertView alloc] initWithTitle:@\"Success\" message:@\"All Review object have been deleted.\" delegate:nil cancelButtonTitle:@\"OK\" otherButtonTitles:nil] show];\n    } else {\n        [[[UIAlertView alloc] initWithTitle:@\"Error\" message:e.errorMessage delegate:nil cancelButtonTitle:@\"OK\" otherButtonTitles:nil] show];\n    }\n}];\n</code></pre>\n</div><div class='example example-android' platform='android' platform_name='Android SDK'><p>Example request to delete all Review objects.</p>\n\n<pre><code>APSCloud.getInstance().sendRequest(\"reviews/batch_delete.json\", \"DELETE\", null, new APSResponseHandler() {\n    public void onResponse(final APSResponse e) {\n        if (e.getSuccess()) {\n            new AlertDialog.Builder(currentActivity)\n            .setTitle(\"Success!\").setMessage(\"All Review objects have been deleted.\")\n            .setPositiveButton(android.R.string.ok, null)\n            .setIcon(android.R.drawable.ic_dialog_info)\n            .show();\n        }\n        else {\n            Log.e(\"APSCloud\", e.getErrorMessage());\n        }\n    }\n\n    @Override\n    public void onException(APSCloudException e) {\n        Log.e(\"APSCloud\", \"Exception throw: \" + e.toString());\n    }\n});\n</code></pre>\n</div></div></div></div></div><div id='method-count' class='member  not-inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><span class='defined-in' rel='Reviews'>Reviews</span></div><strong class='http-method'>GET </strong><a href='#!/api/Reviews-method-count' class='name not-expandable'>reviews/count.json</a></div><div class='description'><div class='short'><p>Retrieves the total number of Review objects.</p>\n</div><div class='long'><p>Retrieves the total number of Review objects.</p>\n<h3 class=\"pa\">Response Parameters</h3><ul><li><span class='pre'>count</span> : Number<div class='sub-desc'><p>Total number of Review objects. Returned in the <code>meta</code> header.</p>\n</div></li></ul><div class='examples-section'><h3 class='examples-title icon-examples'>Examples</h3><div class='example example-titanium' platform='titanium' platform_name='Titanium SDK'><p>Example request to retrieve the total number of Review objects.</p>\n\n<pre><code>Cloud.sendRequest({\n    url : \"reviews/count.json\",\n    method : \"GET\"\n}, function(e) {\n    if (e.success) {\n        alert(e.meta.count);\n    } else {\n        alert('Error:\\n' + ((e.error &amp;&amp; e.message) || JSON.stringify(e)));\n    }\n});\n</code></pre>\n</div><div class='example example-rest' platform='rest' platform_name='REST API'><p>Example cURL request:</p>\n\n<pre><code>$ curl -b cookies.txt -c cookies.txt \"https://api.cloud.appcelerator.com/v1/reviews/count.json?key=&lt;YOUR_APP_KEY&gt;&amp;pretty_json=true\"\n</code></pre>\n\n<p>Example JSON response:</p>\n\n<pre><code>{\n  \"meta\": {\n    \"code\": 200,\n    \"status\": \"ok\",\n    \"method_name\": \"reviewsCount\",\n    \"count\": 1\n  }\n}\n</code></pre>\n</div><div class='example example-ios' platform='ios' platform_name='iOS SDK'><p>Example request to retrieve the total number of Review objects.</p>\n\n<pre><code>[[APSCloud sharedInstance] sendRequest:@\"reviews/count.json\" method:@\"GET\" data:nil handler:^(APSResponse *e){\n    if (e.success) {\n        NSString *msg = [NSString stringWithFormat:@\"Count: %@\", [e.meta valueForKey:@\"count\"]];\n        [[[UIAlertView alloc] initWithTitle:@\"Success\" message:msg delegate:nil cancelButtonTitle:@\"OK\" otherButtonTitles:nil] show];\n    } else {\n        [[[UIAlertView alloc] initWithTitle:@\"Error\" message:e.errorMessage delegate:nil cancelButtonTitle:@\"OK\" otherButtonTitles:nil] show];\n    }\n}];\n</code></pre>\n</div><div class='example example-android' platform='android' platform_name='Android SDK'><p>Example request to retrieve the total number of Review objects.</p>\n\n<pre><code>APSCloud.getInstance().sendRequest(\"reviews/count.json\", \"GET\", null, new APSResponseHandler() {\n    public void onResponse(final APSResponse e) {\n        if (e.getSuccess()) {\n            try {\n                new AlertDialog.Builder(currentActivity)\n                .setTitle(\"Success!\").setMessage(\"Count: \" + Double.toString(e.getMeta().getDouble(\"count\")))\n                .setPositiveButton(android.R.string.ok, null)\n                .setIcon(android.R.drawable.ic_dialog_info)\n                .show();\n            } catch (Exception error) {\n                Log.e(\"APSCloud\", \"Exception throw: \" + error.toString());\n            }\n        }\n        else {\n            Log.e(\"APSCloud\", e.getErrorMessage());\n        }\n    }\n\n    @Override\n    public void onException(APSCloudException e) {\n        Log.e(\"APSCloud\", \"Exception throw: \" + e.toString());\n    }\n});\n</code></pre>\n</div></div></div></div></div><div id='method-create' class='member  not-inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><span class='defined-in' rel='Reviews'>Reviews</span></div><strong class='http-method'>POST </strong><a href='#!/api/Reviews-method-create' class='name expandable'>reviews/create.json</a></div><div class='description'><div class='short'>Adds a review with an optional integer rating. ...</div><div class='long'><p>Adds a review with an optional integer rating. You can also use this API to add\ncomments or likes.</p>\n\n<p>Once an object has one or more reviews (comments) attached to it, it will\nreturn a total review count, rating_count, average rating and a breakdown of\neach rating value:</p>\n\n<pre><code>\"reviews_count\": 2,\n\"ratings_count\": 2,\n\"ratings_average\": 150.0,\n\"ratings_summary\": {\n  \"100\": 1,\n  \"200\": 1\n},\n</code></pre>\n\n<p>To create a review, you must specify a target object using one of the <code>&lt;object&gt;_id</code> parameters,\nsuch as <code>photo_id</code> or <code>post_id</code>. Only one <code>&lt;object&gt;_id</code> parameter may be specified in a request.\nTo specify a <a href=\"#!/api/Users\" rel=\"Users\" class=\"docClass\">User</a> to review, use the the <code>user_object_id</code> parameter.</p>\n\n<p>An application admin can create a review on behalf of another user by\nspecifying that user's ID in the <code>user_id</code> method parameter.</p>\n\n<p>A review must include either <code>content</code> or <code>rating</code>. It can also include both.</p>\n<strong>User Login Required: Yes</strong><h3 class=\"pa\">Parameters</h3><ul><li><span class='pre'>post_id</span> : String<div class='sub-desc'><p>ID of the <a href=\"#!/api/Posts\" rel=\"Posts\" class=\"docClass\">Posts</a> object to review.</p>\n</div></li><li><span class='pre'>photo_id</span> : String<div class='sub-desc'><p>ID of the <a href=\"#!/api/Photos\" rel=\"Photos\" class=\"docClass\">Photos</a> object to review.</p>\n</div></li><li><span class='pre'>user_object_id</span> : String<div class='sub-desc'><p>ID of the <a href=\"#!/api/Users\" rel=\"Users\" class=\"docClass\">Users</a> object to review.</p>\n</div></li><li><span class='pre'>event_id</span> : String<div class='sub-desc'><p>ID of the <a href=\"#!/api/Events\" rel=\"Events\" class=\"docClass\">Events</a> object to review.</p>\n</div></li><li><span class='pre'>place_id</span> : String<div class='sub-desc'><p>ID of the <a href=\"#!/api/Places\" rel=\"Places\" class=\"docClass\">Places</a> object to review.</p>\n</div></li><li><span class='pre'>checkin_id</span> : String<div class='sub-desc'><p>ID of the <a href=\"#!/api/Checkins\" rel=\"Checkins\" class=\"docClass\">Checkins</a> object to review.</p>\n</div></li><li><span class='pre'>review_id</span> : String<div class='sub-desc'><p>ID of the <a href=\"#!/api/Reviews\" rel=\"Reviews\" class=\"docClass\">Reviews</a> object to review.</p>\n</div></li><li><span class='pre'>custom_object_id</span> : String<div class='sub-desc'><p>ID of the <a href=\"#!/api/CustomObjects\" rel=\"CustomObjects\" class=\"docClass\">CustomObjects</a> object to review.</p>\n</div></li><li><span class='pre'>status_id</span> : String<div class='sub-desc'><p>ID of the <a href=\"#!/api/Statuses\" rel=\"Statuses\" class=\"docClass\">Statuses</a> object to review.</p>\n</div></li><li><span class='pre'>content</span> : String<div class='sub-desc'><p>Review or comment text.</p>\n</div></li><li><span class='pre'>rating</span> : String<div class='sub-desc'><p>Rating to be associated with review. You can use \"1\" to represent one <a href=\"#!/api/Likes\" rel=\"Likes\" class=\"docClass\">Like</a>.</p>\n</div></li><li><span class='pre'>allow_duplicate</span> : Boolean<div class='sub-desc'><p>By default, the same user can only submit one review/comment per object.\nSet this flag to <code>true</code> to allow the user to add multiple  reviews or comments to\nthe same object.</p>\n</div></li><li><span class='pre'>tags</span> : String<div class='sub-desc'><p>Comma separated list of tags for this review.</p>\n</div></li><li><span class='pre'>custom_fields</span> : String/Hash<div class='sub-desc'><p>User defined fields. See <a href=\"#!/guide/customfields\">Custom Data Fields</a>.</p>\n</div></li><li><span class='pre'>acl_name</span> : String<div class='sub-desc'><p>Name of an <a href=\"#!/api/ACLs\" rel=\"ACLs\" class=\"docClass\">ACLs</a> to associate with this object.</p>\n\n<p>An ACL can be specified using <code>acl_name</code> or <code>acl_id</code>. The two parameters are\nmutually exclusive.</p>\n</div></li><li><span class='pre'>acl_id</span> : String<div class='sub-desc'><p>ID of an <a href=\"#!/api/ACLs\" rel=\"ACLs\" class=\"docClass\">ACLs</a> to associate with this object.</p>\n\n<p>An ACL can be specified using <code>acl_name</code> or <code>acl_id</code>. The two parameters are\nmutually exclusive.</p>\n</div></li><li><span class='pre'>su_id</span> : String <strong class=\"signature\">admin-only</strong><div class='sub-desc'><p>ID of the <a href=\"#!/api/Users\" rel=\"Users\" class=\"docClass\">Users</a> object to create the review on behalf of.</p>\n\n<p>The currently logged-in user must be an application admin to create a review on\nbehalf of another user.</p>\n</div></li><li><span class='pre'>pretty_json</span> : Boolean<div class='sub-desc'><p>Determines if the JSON response is formatted for readability (<code>true</code>), or displayed on a\nsingle line (<code>false</code>). Default is <code>false</code>.</p>\n</div></li></ul><h3 class=\"pa\">Response Parameters</h3><ul><li><span class='pre'>reviews</span> : <a href=\"#!/api/Reviews\" rel=\"Reviews\" class=\"docClass\">Reviews</a>[]<div class='sub-desc'><p>Single-element array containing the newly-created review.</p>\n</div></li></ul><div class='examples-section'><h3 class='examples-title icon-examples'>Examples</h3><div class='example example-titanium' platform='titanium' platform_name='Titanium SDK'><p>This example creates a new review and checks the response.</p>\n\n<pre><code>Cloud.Reviews.create({\n    post_id: savedPostId,\n    rating: 1,\n    content: 'Good'\n}, function (e) {\n    if (e.success) {\n        var review = e.reviews[0];\n        alert('Success:\\n' +\n            'id: ' + review.id + '\\n' +\n            'rating: ' + review.rating + '\\n' +\n            'content: ' + review.content + '\\n' +\n            'updated_at: ' + review.updated_at);\n    } else {\n        alert('Error:\\n' +\n            ((e.error &amp;&amp; e.message) || JSON.stringify(e)));\n    }\n});\n</code></pre>\n</div><div class='example example-rest' platform='rest' platform_name='REST API'><p>Creates a new Review for the specified Photos object.</p>\n\n<pre><code> curl -F \"pretty_json=true\" -F \"photo_id=5493ad44dda09576ff000004\" -F \"content=nice hair\" -F \"rating=1\" \"http://&lt;host&gt;/v1/reviews/create.json?key=&lt;KEY&gt;&amp;_session_id=&lt;SESSION_ID\"\n\n {\n   \"meta\": {\n     \"code\": 200,\n     \"status\": \"ok\",\n     \"method_name\": \"createReview\"\n   },\n   \"response\": {\n     \"reviews\": [\n       {\n         \"id\": \"5493addbdda09576ff000011\",\n         \"rating\": 1,\n         \"content\": \"nice hair\",\n         \"created_at\": \"2014-12-19T04:47:23+0000\",\n         \"updated_at\": \"2014-12-19T04:47:23+0000\",\n         \"user_id\": \"54928d9fdda0952c2b000001\",\n         \"reviewed_object\": {\n           \"type\": \"Photo\",\n           \"id\": \"5493ad44dda09576ff000004\"\n         }\n       }\n     ]\n   }\n }\n</code></pre>\n</div><div class='example example-ios' platform='ios' platform_name='iOS SDK'><p>Example iOS call:</p>\n\n<pre><code>NSDictionary *data = @{\n                         @\"post_id\" : savedPostId,\n                         @\"rating\" : @1,\n                         @\"content\" : @\"Good\"\n                      };\n[APSReviews create:data withBlock:^(APSResponse *e) {\n    if (e.success){\n      NSArray *reviews = e.response[@\"reviews\"];\n      NSDictionary *review = reviews[0];\n      NSString *message =[NSString stringWithFormat:@\"id : %@\\n rating : %@\\n content : %@\\nUpdated at : %@\\n\",\n                              review[@\"id\"],\n                              review[@\"rating\"],\n                              review[@\"content\"],\n                              review[@\"updated_at\"]];\n\n      [[[UIAlertView alloc] initWithTitle:@\"Success\" \n                                  message:message\n                                 delegate:nil \n                        cancelButtonTitle:@\"OK\" \n                        otherButtonTitles:nil] show];\n    } else {\n        [[[UIAlertView alloc] initWithTitle:@\"Error\"\n                                    message:e.errorMessage\n                                   delegate:nil\n                          cancelButtonTitle:@\"OK\"\n                          otherButtonTitles:nil] show];\n    }\n}];\n</code></pre>\n</div><div class='example example-android' platform='android' platform_name='Android SDK'><p>Example Android APS SDK call:</p>\n\n<pre><code>APSServiceManager.getInstance().enable(getApplicationContext(), 'APS_APP_KEY');\nfinal Activity currentActivity = this;\n\nHashMap&lt;String, Object&gt; data = new HashMap&lt;String, Object&gt;();\ndata.put(\"post_id\", \"4e33a817d9ca724745000003\");\ndata.put(\"rating\", \"1\");\ndata.put(\"content\", \"Good\");\n\ntry {\n    APSReviews.create(data, new APSResponseHandler() {\n\n        @Override\n        public void onResponse(final APSResponse e) {\n            if (e.getSuccess()) {\n                new AlertDialog.Builder(currentActivity)\n                .setTitle(\"Success\").setMessage(\"Created!\")\n                .setPositiveButton(android.R.string.ok, null)\n                .setIcon(android.R.drawable.ic_dialog_info)\n                .show();\n            } else {\n                Log.e(\"APSReviews\", \"ERROR: \" + e.getErrorMessage());\n            }\n        }\n\n        @Override\n        public void onException(final APSCloudException e) {\n            Log.e(\"APSReviews\", \"Exception throw: \" + e.toString());\n        }\n    });\n} catch (APSCloudException e) {\n    Log.e(\"APSReviews\", \"Exception thrown: \" + e.toString());\n}\n</code></pre>\n</div></div></div></div></div><div id='method-delete' class='member  not-inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><span class='defined-in' rel='Reviews'>Reviews</span></div><strong class='http-method'>DELETE </strong><a href='#!/api/Reviews-method-delete' class='name expandable'>reviews/delete.json</a></div><div class='description'><div class='short'>Delete the review (comment) with the given id. ...</div><div class='long'><p>Delete the review (comment) with the given <code>id</code>. Only the original submitter\ncan delete the review. If the review has a rating attached to\nit, deleting the review will update the average rating and rating summary.</p>\n\n<p>To delete a review, you must specify <strong>both</strong> the ID of the review and the ID of\nthe reviewed object (<a href=\"#!/api/Reviews-property-post\" rel=\"Reviews-property-post\" class=\"docClass\">Post</a>, <a href=\"#!/api/Reviews-property-photo\" rel=\"Reviews-property-photo\" class=\"docClass\">Photo</a>, <a href=\"#!/api/Reviews-property-user\" rel=\"Reviews-property-user\" class=\"docClass\">User</a>, <a href=\"#!/api/Reviews-property-event\" rel=\"Reviews-property-event\" class=\"docClass\">Event</a>,\n<a href=\"#!/api/Reviews-property-checkin\" rel=\"Reviews-property-checkin\" class=\"docClass\">Checkin</a>, <a href=\"#!/api/Reviews-property-place\" rel=\"Reviews-property-place\" class=\"docClass\">Place</a>, <a href=\"#!/api/Reviews-property-custom_object\" rel=\"Reviews-property-custom_object\" class=\"docClass\">CustomObject</a>,\n<a href=\"#!/api/Reviews-property-status\" rel=\"Reviews-property-status\" class=\"docClass\">Status</a>, or <a href=\"#!/api/Reviews-property-review\" rel=\"Reviews-property-review\" class=\"docClass\">Review</a>. The reviewed object is not deleted, however.</p>\n\n<p>An application admin can delete any Review object.</p>\n<strong>User Login Required: Yes</strong><h3 class=\"pa\">Parameters</h3><ul><li><span class='pre'>review_id</span> : String <strong class=\"required signature\">required</strong><div class='sub-desc'><p>Review object to delete.</p>\n</div></li><li><span class='pre'>pretty_json</span> : Boolean<div class='sub-desc'><p>Determines if the JSON response is formatted for readability (<code>true</code>), or displayed on a\nsingle line (<code>false</code>). Default is <code>false</code>.</p>\n</div></li></ul><div class='examples-section'><h3 class='examples-title icon-examples'>Examples</h3><div class='example example-titanium' platform='titanium' platform_name='Titanium SDK'><p>This example deletes a review and checks the response.</p>\n\n<pre><code>Cloud.Reviews.remove({\n    post_id: savedPostId,\n    review_id: savedReviewId\n}, function (e) {\n    if (e.success) {\n        alert('Success');\n    } else {\n        alert('Error:\\n' +\n            ((e.error &amp;&amp; e.message) || JSON.stringify(e)));\n    }\n});\n</code></pre>\n</div><div class='example example-rest' platform='rest' platform_name='REST API'><p>Example cURL request:</p>\n\n<pre><code>$ curl -b cookies.txt -c cookies.txt -X DELETE \"https://api.cloud.appcelerator.com/v1/reviews/delete.json?key=&lt;YOUR APP APP KEY&gt;&amp;pretty_json=true&amp;post_id=4e33a817d9ca724745000003&amp;review_id=4e33a817d9ca724745000006\"\n</code></pre>\n\n<p>Example JSON response:</p>\n\n<pre><code>{\n  \"meta\": {\n    \"status\":\"ok\",\n    \"code\":200\n  }\n}\n</code></pre>\n</div><div class='example example-ios' platform='ios' platform_name='iOS SDK'><p>Example iOS call:</p>\n\n<pre><code>NSDictionary *data = @{\n                         @\"post_id\" : savedPostId,\n                         @\"review_id\" : savedReviewId\n                      };\n[APSReviews remove:data withBlock:^(APSResponse *e) {\n    if (e.success){\n      [[[UIAlertView alloc] initWithTitle:@\"Success\" \n                                  message:message\n                                 delegate:nil \n                        cancelButtonTitle:@\"OK\" \n                        otherButtonTitles:nil] show];\n    } else {\n        [[[UIAlertView alloc] initWithTitle:@\"Error\"\n                                    message:e.errorMessage\n                                   delegate:nil\n                          cancelButtonTitle:@\"OK\"\n                          otherButtonTitles:nil] show];\n    }\n}];\n</code></pre>\n</div><div class='example example-android' platform='android' platform_name='Android SDK'><p>Example Android APS SDK call:</p>\n\n<pre><code>HashMap&lt;String, Object&gt; data = new HashMap&lt;String, Object&gt;();\ndata.put(\"post_id\", \"4e33a817d9ca724745000003\");\ndata.put(\"review_id\", \"4e33a817d9ca724745000006\");\n\ntry {\n\n    APSReviews.remove(data, new APSResponseHandler() {\n\n        @Override\n        public void onResponse(final APSResponse e) {\n            if (e.getSuccess()) {\n                new AlertDialog.Builder(currentActivity)\n                .setTitle(\"Success!\").setMessage(\"Removed!\")\n                .setPositiveButton(android.R.string.ok, null)\n                .setIcon(android.R.drawable.ic_dialog_info)\n                .show();\n            } else {\n               Log.e(\"APSReviews\", \"ERROR: \" + e.getErrorMessage());\n            }\n        }\n\n        @Override\n        public void onException(final APSCloudException e) {\n            Log.e(\"APSReviews\", \"Exception throw: \" + e.toString());\n        }\n    });\n} catch (APSCloudException e) {\n    Log.e(\"APSReviews\", \"Exception thrown: \" + e.toString());\n}\n</code></pre>\n</div></div></div></div></div><div id='method-query' class='member  not-inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><span class='defined-in' rel='Reviews'>Reviews</span></div><strong class='http-method'>GET </strong><a href='#!/api/Reviews-method-query' class='name expandable'>reviews/query.json</a></div><div class='description'><div class='short'>Perform custom query of reviews with sorting and paginating. ...</div><div class='long'><p>Perform custom query of reviews with sorting and paginating. Currently you can\nnot query or sort data stored inside array or hash in custom fields.</p>\n\n<p>The query must be limited to reviews of a given object (by specifying one of <code>post_id</code>,\n<code>photo_id</code>, etc.) or limited to reviews generated by a given user (by specifying\n<code>owner_id</code>.</p>\n\n<p>In addition to custom fields, the following pre-defined fields can be used to\nquery and sort reviews:</p>\n\n<ul>\n<li><code>user_id</code> : <code>String</code>. Review owner's user ID.</li>\n<li><code>rating</code> : <code>Integer</code>. Rating assigned to this review.</li>\n<li><code>tags_array</code> : <code>String</code>. Tags associated with the review.</li>\n<li><code>created_at</code> : <code>Date</code>. Timestamp when the review was created.</li>\n<li><code>updated_at</code> : <code>Date</code>. Timestamp when the review was last updated.</li>\n</ul>\n\n\n<p>In ArrowDB 1.1.5 and later, you can paginate query results using <code>skip</code> and <code>limit</code> parameters, or by including\na <code>where</code> clause to limit the results to objects whose IDs fall within a specified range.\nFor details, see <a href=\"#!/guide/search_query-section-query-pagination\">Query Pagination</a>.</p>\n\n<p>For details about using the query parameters,\nsee the <a href=\"#!/guide/search_query\">Search and Query guide</a>.</p>\n<h3 class=\"pa\">Parameters</h3><ul><li><span class='pre'>post_id</span> : String<div class='sub-desc'><p>Limit query to reviews on the identified Post object.</p>\n</div></li><li><span class='pre'>photo_id</span> : String<div class='sub-desc'><p>Limit query to reviews on the identified Photo object.</p>\n</div></li><li><span class='pre'>user_id</span> : String<div class='sub-desc'><p>Limit query to reviews on the identified User object.</p>\n</div></li><li><span class='pre'>event_id</span> : String<div class='sub-desc'><p>Limit query to reviews on the identified Event object.</p>\n</div></li><li><span class='pre'>place_id</span> : String<div class='sub-desc'><p>Limit query to reviews on the identified Place object.</p>\n</div></li><li><span class='pre'>checkin_id</span> : String<div class='sub-desc'><p>Limit query to reviews on the identified Checkin object.</p>\n</div></li><li><span class='pre'>review_id</span> : String<div class='sub-desc'><p>Limit query to reviews on the identified Review object.</p>\n</div></li><li><span class='pre'>custom_object_id</span> : String<div class='sub-desc'><p>Limit query to reviews on the identified Custom object.</p>\n</div></li><li><span class='pre'>status_id</span> : String<div class='sub-desc'><p>Limit query to reviews on the identified Status object.</p>\n</div></li><li><span class='pre'>owner_id</span> : String<div class='sub-desc'><p>Limit query results to reviews submitted by the identified  user.</p>\n</div></li><li><span class='pre'>page</span> : Number<div class='sub-desc'><p class=\"note\">\nStarting in ArrowDB 1.1.5, page and per_page are no longer supported in query operations. \nApplications should instead use <strong>skip</strong> and <strong>limit</strong> \nquery parameters.\n</p>\n\n</div></li><li><span class='pre'>per_page</span> : Number<div class='sub-desc'><p class=\"note\">\nStarting in ArrowDB 1.1.5, page and per_page are no longer supported in query operations. \nApplications should instead use <strong>skip</strong> and <strong>limit</strong> \nquery parameters.\n</p>\n\n</div></li><li><span class='pre'>limit</span> : Number<div class='sub-desc'><p>The number of records to fetch. The value must be greater than 0, and no greater than\n1000, or an HTTP 400 (Bad Request) error will be returned. Default value of <code>limit</code> is 10.</p>\n</div></li><li><span class='pre'>skip</span> : Number<div class='sub-desc'><p>The number of records to skip. The value must be greater than or equal to 0, and no greater\nthan 4999, or an HTTP 400 error will be returned. To skip 5000 records or more\nyou need to perform a range-based query. See\n<a href=\"#!/guide/search_query-section-query-pagination\">Query Pagination</a> for more information.</p>\n</div></li><li><span class='pre'>where</span> : Hash<div class='sub-desc'><p>Constraint values for fields. <code>where</code> should be encoded JSON.</p>\n\n<p>If <code>where</code> is not specified, <code>query</code> returns all objects.</p>\n</div></li><li><span class='pre'>order</span> : String<div class='sub-desc'><p>Sort results by one or more fields.</p>\n</div></li><li><span class='pre'>sel</span> : Hash<div class='sub-desc'><p>Selects the object fields to display. Do not use this parameter with <code>unsel</code>.</p>\n</div></li><li><span class='pre'>show_user_like</span> : Boolean<div class='sub-desc'><p>If set to <strong>true</strong>, each Review object in the response includes <code>\"current_user_liked: true\"</code>\n if the current user has liked the object. If the user has not liked the object, the\n<code>current_user_liked</code> field is not included in the response.</p>\n</div></li><li><span class='pre'>unsel</span> : Hash<div class='sub-desc'><p>Selects the object fields NOT to display. Do not use this parameter with <code>sel</code>.</p>\n</div></li><li><span class='pre'>response_json_depth</span> : Number<div class='sub-desc'><p>Nested object depth level counts in the response JSON.</p>\n\n<p>In order to reduce server API calls from an application, the response JSON may\ninclude not just the objects that are being queried/searched, but also\nsome important data related to the returned objects, such as owners and\nreferenced objects.</p>\n\n<p>Default is 1, valid range is 1 to 8.</p>\n</div></li><li><span class='pre'>pretty_json</span> : Boolean<div class='sub-desc'><p>Determines if the JSON response is formatted for readability (<code>true</code>), or displayed on a\nsingle line (<code>false</code>). Default is <code>false</code>.</p>\n</div></li></ul><h3 class=\"pa\">Response Parameters</h3><ul><li><span class='pre'>reviews</span> : <a href=\"#!/api/Reviews\" rel=\"Reviews\" class=\"docClass\">Reviews</a>[]<div class='sub-desc'><p>List of reviews matching the query parameters.</p>\n</div></li></ul><div class='examples-section'><h3 class='examples-title icon-examples'>Examples</h3><div class='example example-titanium' platform='titanium' platform_name='Titanium SDK'><p>This example requests a list of reviews and checks the response.</p>\n\n<pre><code>Cloud.Reviews.query({\n    page: 1,\n    per_page: 20,\n    where: {\n        rating: { '$gt': 5.0 }\n    }\n}, function (e) {\n    if (e.success) {\n        alert('Success:\\n' +\n            'Count: ' + e.reviews.length);\n        for (var i = 0; i &lt; e.reviews.length; i++) {\n            var review = e.reviews[i];\n            alert('id: ' + review.id + '\\n' +\n                'rating: ' + review.rating + '\\n' +\n                'content: ' + review.content + '\\n' +\n                'updated_at: ' + review.updated_at);\n        }\n    } else {\n        alert('Error:\\n' +\n            ((e.error &amp;&amp; e.message) || JSON.stringify(e)));\n    }\n});\n</code></pre>\n</div><div class='example example-rest' platform='rest' platform_name='REST API'><p>Queries for reviews with a rating of \"5\" that were created by the user specified by the\n<code>owner_id</code> parameter.</p>\n\n<pre><code>$ curl -c cookies.txt -b cookies.txt -X GET --data-urlencode 'where={\"rating\":5.0}}' \"https://&lt;HOST&gt;/v1/reviews/query.json?owner_id=54bd6e5e8a1b3c092949f265&amp;key=&lt;KEY&gt;&amp;host=api.cloud.appcelerator.com&amp;pretty_json=true\"\n\n{\n  \"meta\": {\n    \"code\": 200,\n    \"status\": \"ok\",\n    \"method_name\": \"queryReviews\"\n  },\n  \"response\": {\n    \"reviews\": [\n      {\n        \"id\": \"54bd6f08351b7909384bc1c1\",\n        \"rating\": 5,\n        \"created_at\": \"2015-01-19T20:54:32+0000\",\n        \"updated_at\": \"2015-01-19T20:54:32+0000\",\n        \"user_id\": \"54bd6e5e8a1b3c092949f265\",\n        \"reviewed_object\": {\n          \"type\": \"Photo\",\n          \"id\": \"54bd6f012eb3b409394c3a6d\"\n        },\n        \"custom_fields\": {\n        }\n      },\n      {\n        \"id\": \"54bd6ea32eb3b409434d86d5\",\n        \"rating\": 5,\n        \"created_at\": \"2015-01-19T20:52:51+0000\",\n        \"updated_at\": \"2015-01-19T20:52:51+0000\",\n        \"user_id\": \"54bd6e5e8a1b3c092949f265\",\n        \"reviewed_object\": {\n          \"type\": \"Status\",\n          \"id\": \"54bd68a2351b7909404f245d\"\n        },\n        \"custom_fields\": {\n        }\n      },\n      {\n        \"id\": \"54bd68d42eb3b409434d5547\",\n        \"rating\": 5,\n        \"created_at\": \"2015-01-19T20:28:04+0000\",\n        \"updated_at\": \"2015-01-19T20:31:17+0000\",\n        \"user_id\": \"54ab2a07d3a37bd24a053578\",\n        \"reviewed_object\": {\n          \"type\": \"Status\",\n          \"id\": \"a23468a2351b7909404f245d\"\n        },\n        \"custom_fields\": {\n        }\n      }\n    ]\n  }\n}\n</code></pre>\n</div><div class='example example-ios' platform='ios' platform_name='iOS SDK'><p>Example iOS call:</p>\n\n<pre><code>NSDictionary *data = @{\n                         @\"page\" : @1,\n                         @\"per_page\" : @20,\n                         @\"where\" : @{\n                            @\"$gt\" : @5.0\n                         }\n                      };\n[APSReviews query:data withBlock:^(APSResponse *e) {\n    if (e.success){\n        NSArray *reviews = e.response[@\"reviews\"];\n        for (NSDictionary *review in reviews) {\n          NSString *message =[NSString stringWithFormat:@\"id : %@\\n rating : %@\\n content : %@\\nUpdated at : %@\\n\",\n                                  review[@\"id\"],\n                                  review[@\"rating\"],\n                                  review[@\"content\"],\n                                  review[@\"updated_at\"]];\n\n          [[[UIAlertView alloc] initWithTitle:@\"Success\" \n                                      message:message\n                                     delegate:nil \n                            cancelButtonTitle:@\"OK\" \n                            otherButtonTitles:nil] show];\n        }\n    } else {\n        [[[UIAlertView alloc] initWithTitle:@\"Error\"\n                                    message:e.errorMessage\n                                   delegate:nil\n                          cancelButtonTitle:@\"OK\"\n                          otherButtonTitles:nil] show];\n    }\n}];\n</code></pre>\n</div><div class='example example-android' platform='android' platform_name='Android SDK'><p>Example Android APS SDK call:</p>\n\n<pre><code>APSServiceManager.getInstance().enable(getApplicationContext(), 'APS_APP_KEY');\n\ntry {\n    HashMap&lt;String, Object&gt; data = new HashMap&lt;String, Object&gt;();\n    JSONObject params = new JSONObject(\"{'rating': {'$gt': 7.0}}\");\n    data.put(\"where\", params);\n\n    APSReviews.query(data, new APSResponseHandler() {\n\n        @Override\n        public void onResponse(final APSResponse e) {\n            if (e.getSuccess()) {\n                try {\n                    JSONArray reviews = e.getResponse().getJSONArray(\"reviews\");\n                    if (reviews.length() &lt;= 0) {\n                        Log.i(\"APSReviews\", \"No Results!\");\n                    } else {\n                        for (int i = 0; i &lt; reviews.length(); i++) {\n                            JSONObject review = reviews.getJSONObject(i);\n                            Log.i(\"APSReviews\", review.getString(\"rating\") + \":\" + review.getString(\"content\"));\n                        }\n                    }\n                } catch (JSONException err) {\n                    Log.e(\"APSReviews\", \"Exception thrown: \" + err.toString());\n                }\n            } else {\n                Log.e(\"APSReviews\", \"ERROR: \" + e.getErrorMessage());\n            }\n        }\n\n        @Override\n        public void onException(final APSCloudException e) {\n            Log.e(\"APSReviews\", \"Exception throw: \" + e.toString());\n        }\n    });\n} catch (APSCloudException e) {\n    Log.e(\"APSReviews\", \"Exception thrown: \" + e.toString());\n} catch (JSONException e) {\n    Log.e(\"APSReviews\", \"Exception thrown: \" + e.toString());\n}\n</code></pre>\n</div></div></div></div></div><div id='method-show' class='member  not-inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><span class='defined-in' rel='Reviews'>Reviews</span></div><strong class='http-method'>GET </strong><a href='#!/api/Reviews-method-show' class='name expandable'>reviews/show.json</a></div><div class='description'><div class='short'>Shows the review with the given id. ...</div><div class='long'><p>Shows the review with the given <code>id</code>.</p>\n<h3 class=\"pa\">Parameters</h3><ul><li><span class='pre'>review_id</span> : String <strong class=\"required signature\">required</strong><div class='sub-desc'><p>Review object to show.</p>\n</div></li><li><span class='pre'>show_user_like</span> : Boolean<div class='sub-desc'><p>If set to <strong>true</strong> the Review object in the response will include <code>\"current_user_liked: true\"</code>\nif the current user has liked the object. If the user has not liked the object, the\n<code>current_user_liked</code> field is not included in the response.</p>\n</div></li><li><span class='pre'>response_json_depth</span> : Number<div class='sub-desc'><p>Nested object depth level counts in response JSON.</p>\n\n<p>In order to reduce server API calls from an application, the response JSON may\ninclude not just the objects that are being queried/searched, but also\nsome important data related to the returned objects such as object's owner or\nreferenced objects.</p>\n\n<p>Default is 1, valid range is 1 to 8.</p>\n</div></li><li><span class='pre'>pretty_json</span> : Boolean<div class='sub-desc'><p>Determines if the JSON response is formatted for readability (<code>true</code>), or displayed on a\nsingle line (<code>false</code>). Default is <code>false</code>.</p>\n</div></li></ul><h3 class=\"pa\">Response Parameters</h3><ul><li><span class='pre'>reviews</span> : <a href=\"#!/api/Reviews\" rel=\"Reviews\" class=\"docClass\">Reviews</a>[]<div class='sub-desc'><p>Single-element array containing the identified review.</p>\n</div></li></ul><div class='examples-section'><h3 class='examples-title icon-examples'>Examples</h3><div class='example example-titanium' platform='titanium' platform_name='Titanium SDK'><p>This example retrieves information about a review and checks the response.</p>\n\n<pre><code>Cloud.Reviews.show({\n    post_id: savedPostId,\n    review_id: savedReviewId\n}, function (e) {\n    if (e.success) {\n        var review = e.reviews[0];\n        alert('Success:\\n' +\n            'id: ' + review.id + '\\n' +\n            'rating: ' + review.rating + '\\n' +\n            'content: ' + review.content + '\\n' +\n            'updated_at: ' + review.updated_at);\n    } else {\n        alert('Error:\\n' +\n            ((e.error &amp;&amp; e.message) || JSON.stringify(e)));\n    }\n});\n</code></pre>\n</div><div class='example example-rest' platform='rest' platform_name='REST API'><p>Shows the specified review.</p>\n\n<pre><code>$ curl -b cookies.txt -c cookies.txt \"https://&lt;HOST&gt;/v1/reviews/show.json?key=&lt;YOUR_APP_KEY&gt;&amp;pretty_json=true&amp;review_id=54bd68d42eb3b409434d5547\n</code></pre>\n\n<p>Example JSON response:</p>\n\n<pre><code>{\n  \"meta\": {\n    \"code\": 200,\n    \"status\": \"ok\",\n    \"method_name\": \"showReview\"\n  },\n  \"response\": {\n    \"reviews\": [\n      {\n        \"id\": \"54bd68d42eb3b409434d5547\",\n        \"rating\": 3,\n        \"created_at\": \"2015-01-19T20:28:04+0000\",\n        \"updated_at\": \"2015-01-19T20:31:17+0000\",\n        \"reviewed_object\": {\n          \"type\": \"Status\",\n          \"id\": \"54bd68a2351b7909404f245d\"\n        },\n        \"custom_fields\": {\n        },\n        \"user\": {\n          \"id\": \"54ab2a07d3a37bd24a053578\",\n          \"created_at\": \"2015-01-06T00:19:19+0000\",\n          \"updated_at\": \"2015-01-19T20:21:14+0000\",\n          \"external_accounts\": [\n\n          ],\n          \"confirmed_at\": \"2015-01-06T00:19:19+0000\",\n          \"username\": \"tim\",\n          \"admin\": \"false\",\n          \"stats\": {\n            \"photos\": {\n              \"total_count\": 0\n            },\n            \"storage\": {\n              \"used\": 0\n            }\n          },\n          \"friend_counts\": {\n            \"requests\": 0,\n            \"friends\": 0\n          }\n        }\n      }\n    ]\n  }\n}\n</code></pre>\n</div><div class='example example-ios' platform='ios' platform_name='iOS SDK'><p>Example iOS call:</p>\n\n<pre><code>NSDictionary *data = @{\n                         @\"post_id\" : savedPostId,\n                         @\"review_id\" : savedReviewId\n                      };\n[APSReviews show:data withBlock:^(APSResponse *e) {\n    if (e.success){\n      NSArray *reviews = e.response[@\"reviews\"];\n      NSDictionary *review = reviews[0];\n      NSString *message =[NSString stringWithFormat:@\"id : %@\\n rating : %@\\n content : %@\\nUpdated at : %@\\n\",\n                              review[@\"id\"],\n                              review[@\"rating\"],\n                              review[@\"content\"],\n                              review[@\"updated_at\"]];\n\n      [[[UIAlertView alloc] initWithTitle:@\"Success\" \n                                  message:message\n                                 delegate:nil \n                        cancelButtonTitle:@\"OK\" \n                        otherButtonTitles:nil] show];\n    } else {\n        [[[UIAlertView alloc] initWithTitle:@\"Error\"\n                                    message:e.errorMessage\n                                   delegate:nil\n                          cancelButtonTitle:@\"OK\"\n                          otherButtonTitles:nil] show];\n    }\n}];\n</code></pre>\n</div><div class='example example-android' platform='android' platform_name='Android SDK'><p>Example Android call:</p>\n\n<pre><code>APSServiceManager.getInstance().enable(getApplicationContext(), 'APS_APP_KEY');\n\nHashMap&lt;String, Object&gt; data = new HashMap&lt;String, Object&gt;();\ndata.put(\"post_id\", \"4e33a817d9ca724745000003\");\ndata.put(\"review_id\", \"4e33a9f5d9ca724769000006\");\n\ntry {\n    APSReviews.show(data, new APSResponseHandler() {\n\n        @Override\n        public void onResponse(final APSResponse e) {\n            if (e.getSuccess()) {\n                Log.i(\"APSReviews\", e.getResponseString());\n            } else {\n                Log.e(\"APSReviews\", \"ERROR: \" + e.getErrorMessage());\n            }\n         }\n\n        @Override\n        public void onException(final APSCloudException e) {\n            Log.e(\"APSReviews\", \"Exception throw: \" + e.toString());\n        }\n    });\n} catch (APSCloudException e) {\n    Log.e(\"APSReviews\", \"Exception thrown: \" + e.toString());\n}\n</code></pre>\n</div></div></div></div></div><div id='method-update' class='member  not-inherited'><a href='#' class='side expandable'><span>&nbsp;</span></a><div class='title'><div class='meta'><span class='defined-in' rel='Reviews'>Reviews</span></div><strong class='http-method'>PUT </strong><a href='#!/api/Reviews-method-update' class='name expandable'>reviews/update.json</a></div><div class='description'><div class='short'>Updates the review with the given id. ...</div><div class='long'><p>Updates the review with the given <code>id</code>.</p>\n\n<p>Ordinary users can update reviews they own or have update access to.</p>\n\n<p>An application admin can update a Review on behalf of another user by\nspecifying that user's ID in the <code>user_id</code> method parameter.</p>\n<strong>User Login Required: Yes</strong><h3 class=\"pa\">Parameters</h3><ul><li><span class='pre'>post_id</span> : String<div class='sub-desc'><p>ID of the <a href=\"#!/api/Posts\" rel=\"Posts\" class=\"docClass\">Posts</a> object to review.</p>\n</div></li><li><span class='pre'>photo_id</span> : String<div class='sub-desc'><p>ID of the <a href=\"#!/api/Photos\" rel=\"Photos\" class=\"docClass\">Photos</a> object to review.</p>\n</div></li><li><span class='pre'>user_object_id</span> : String<div class='sub-desc'><p>ID of the <a href=\"#!/api/Users\" rel=\"Users\" class=\"docClass\">Users</a> object to review.</p>\n</div></li><li><span class='pre'>event_id</span> : String<div class='sub-desc'><p>ID of the <a href=\"#!/api/Events\" rel=\"Events\" class=\"docClass\">Events</a> object to review.</p>\n</div></li><li><span class='pre'>place_id</span> : String<div class='sub-desc'><p>ID of the <a href=\"#!/api/Places\" rel=\"Places\" class=\"docClass\">Places</a> object to review.</p>\n</div></li><li><span class='pre'>checkin_id</span> : String<div class='sub-desc'><p>ID of the <a href=\"#!/api/Checkins\" rel=\"Checkins\" class=\"docClass\">Checkins</a> object to review.</p>\n</div></li><li><span class='pre'>review_object_id</span> : String<div class='sub-desc'><p>ID of the <a href=\"#!/api/Reviews\" rel=\"Reviews\" class=\"docClass\">Reviews</a> object to review.</p>\n</div></li><li><span class='pre'>custom_object_id</span> : String<div class='sub-desc'><p>ID of the <a href=\"#!/api/CustomObjects\" rel=\"CustomObjects\" class=\"docClass\">CustomObjects</a> object to review.</p>\n</div></li><li><span class='pre'>status_id</span> : String<div class='sub-desc'><p>ID of the <a href=\"#!/api/Statuses\" rel=\"Statuses\" class=\"docClass\">Statuses</a> object to review.</p>\n</div></li><li><span class='pre'>review_id</span> : String <strong class=\"required signature\">required</strong><div class='sub-desc'><p>ID of the Review object to update.</p>\n</div></li><li><span class='pre'>content</span> : String<div class='sub-desc'><p>Review or comment text.</p>\n</div></li><li><span class='pre'>rating</span> : String<div class='sub-desc'><p>Rating to be associated with review. You can use \"1\" to represent one <a href=\"#!/api/Likes\" rel=\"Likes\" class=\"docClass\">Like</a>.</p>\n</div></li><li><span class='pre'>su_id</span> : String <strong class=\"signature\">admin-only</strong><div class='sub-desc'><p>ID of the <a href=\"#!/api/Users\" rel=\"Users\" class=\"docClass\">Users</a> object to update the review on behalf of. The currently\nlogged-in user must be an application admin to create a review on\nbehalf of another user.</p>\n</div></li><li><span class='pre'>allow_duplicate</span> : Boolean<div class='sub-desc'><p>By default, the same user can only submit one review/comment per object.\nSet this flag to <code>true</code> to allow the user to add multiple  reviews or comments to\nthe same object.</p>\n</div></li><li><span class='pre'>tags</span> : String<div class='sub-desc'><p>Comma separated list of tags for this review.</p>\n</div></li><li><span class='pre'>custom_fields</span> : String/Hash<div class='sub-desc'><p>User defined fields. See <a href=\"#!/guide/customfields\">Custom Data Fields</a>.</p>\n</div></li><li><span class='pre'>acl_name</span> : String<div class='sub-desc'><p>Name of an <a href=\"#!/api/ACLs\" rel=\"ACLs\" class=\"docClass\">ACLs</a> to associate with this object.</p>\n\n<p>An ACL can be specified using <code>acl_name</code> or <code>acl_id</code>. The two parameters are\nmutually exclusive.</p>\n</div></li><li><span class='pre'>acl_id</span> : String<div class='sub-desc'><p>ID of an <a href=\"#!/api/ACLs\" rel=\"ACLs\" class=\"docClass\">ACLs</a> to associate with this object.</p>\n\n<p>An ACL can be specified using <code>acl_name</code> or <code>acl_id</code>. The two parameters are\nmutually exclusive.</p>\n</div></li><li><span class='pre'>pretty_json</span> : Boolean<div class='sub-desc'><p>Determines if the JSON response is formatted for readability (<code>true</code>), or displayed on a\nsingle line (<code>false</code>). Default is <code>false</code>.</p>\n</div></li></ul><h3 class=\"pa\">Response Parameters</h3><ul><li><span class='pre'>reviews</span> : <a href=\"#!/api/Reviews\" rel=\"Reviews\" class=\"docClass\">Reviews</a>[]<div class='sub-desc'><p>Single-element array containing the updated review.</p>\n</div></li></ul><div class='examples-section'><h3 class='examples-title icon-examples'>Examples</h3><div class='example example-titanium' platform='titanium' platform_name='Titanium SDK'><p>This example updates a review and checks the response.</p>\n\n<pre><code>Cloud.Reviews.update({\n    post_id: savedPostId,\n    review_id: savedReviewId,\n    rating: 100,\n    content: 'Very good!'\n}, function (e) {\n    if (e.success) {\n        var review = e.reviews[0];\n        alert('Success:\\n' +\n            'id: ' + review.id + '\\n' +\n            'rating: ' + review.rating + '\\n' +\n            'content: ' + review.content + '\\n' +\n            'updated_at: ' + review.updated_at);\n    } else {\n        alert('Error:\\n' +\n            ((e.error &amp;&amp; e.message) || JSON.stringify(e)));\n    }\n});\n</code></pre>\n</div><div class='example example-rest' platform='rest' platform_name='REST API'><p>Example cURL request:</p>\n\n<pre><code>$ curl -b cookies.txt -c cookies.txt -X PUT -F \"status_id=54bd7a9b8a1b3c09334fae75\" -F \"review_id=4e33a9f5d9ca724769000006\" -F \"rating=9\" -F \"content=cool hair\" https://api.cloud.appcelerator.com/v1/reviews/update.json?key=&lt;YOUR APP APP KEY&gt;&amp;pretty_json=true\n</code></pre>\n\n<p>Example JSON response:</p>\n\n<pre><code>{\n  \"meta\": {\n    \"code\": 200,\n    \"status\": \"ok\",\n    \"method_name\": \"updateReview\"\n  },\n  \"response\": {\n    \"reviews\": [\n      {\n        \"id\": \"54bd7aa58a1b3c09294a5cd6\",\n        \"rating\": 9,\n        \"content\": \"cool hair\",\n        \"created_at\": \"2015-01-19T21:44:05+0000\",\n        \"updated_at\": \"2015-01-19T21:49:12+0000\",\n        \"reviewed_object\": {\n          \"type\": \"Status\",\n          \"id\": \"54bd7a9b8a1b3c09334fae75\"\n        },\n        \"custom_fields\": {\n        },\n        \"user\": {\n          \"id\": \"54bd6e5e8a1b3c092949f265\",\n          \"created_at\": \"2015-01-19T20:51:42+0000\",\n          \"updated_at\": \"2015-01-19T20:51:42+0000\",\n          \"external_accounts\": [\n\n          ],\n          \"confirmed_at\": \"2015-01-19T20:51:42+0000\",\n          \"username\": \"abc\",\n          \"admin\": \"false\",\n          \"stats\": {\n            \"photos\": {\n              \"total_count\": 0\n            },\n            \"storage\": {\n              \"used\": 0\n            }\n          },\n          \"friend_counts\": {\n            \"requests\": 0,\n            \"friends\": 0\n          }\n        }\n      }\n    ]\n  }\n}\n</code></pre>\n</div><div class='example example-ios' platform='ios' platform_name='iOS SDK'><p>Example iOS call:</p>\n\n<pre><code>NSDictionary *data = @{\n                         @\"post_id\" : savedPostId,\n                         @\"review_id\" : savedReviewId,\n                         @\"rating\" : @100,\n                         @\"content\" : @\"Very good!\"\n                      };\n[APSReviews update:data withBlock:^(APSResponse *e) {\n    if (e.success){\n      NSArray *reviews = e.response[@\"reviews\"];\n      NSDictionary *review = reviews[0];\n      NSString *message =[NSString stringWithFormat:@\"id : %@\\n rating : %@\\n content : %@\\nUpdated at : %@\\n\",\n                              review[@\"id\"],\n                              review[@\"rating\"],\n                              review[@\"content\"],\n                              review[@\"updated_at\"]];\n\n      [[[UIAlertView alloc] initWithTitle:@\"Success\" \n                                  message:message\n                                 delegate:nil \n                        cancelButtonTitle:@\"OK\" \n                        otherButtonTitles:nil] show];\n    } else {\n        [[[UIAlertView alloc] initWithTitle:@\"Error\"\n                                    message:e.errorMessage\n                                   delegate:nil\n                          cancelButtonTitle:@\"OK\"\n                          otherButtonTitles:nil] show];\n    }\n}];\n</code></pre>\n</div><div class='example example-android' platform='android' platform_name='Android SDK'><p>Example Android call:</p>\n\n<pre><code>APSServiceManager.getInstance().enable(getApplicationContext(), 'APS_APP_KEY');\nfinal Activity currentActivity = this;\n\nHashMap&lt;String, Object&gt; data = new HashMap&lt;String, Object&gt;();\ndata.put(\"post_id\", \"4e33a817d9ca724745000003\");\ndata.put(\"review_id\", \"4e33a9f5d9ca724769000006\");\ndata.put(\"rating\", \"1\");\ndata.put(\"content\", \"Good\");\n\ntry {\n    APSReviews.update(data, new APSResponseHandler() {\n\n        @Override\n        public void onResponse(final APSResponse e) {\n            if (e.getSuccess()) {\n                new AlertDialog.Builder(currentActivity)\n                .setTitle(\"Success\").setMessage(\"Updated!\")\n                .setPositiveButton(android.R.string.ok, null)\n                .setIcon(android.R.drawable.ic_dialog_info)\n                .show();\n            } else {\n                Log.e(\"APSReviews\", \"ERROR: \" + e.getErrorMessage());\n            }\n        }\n\n        @Override\n        public void onException(final APSCloudException e) {\n            Log.e(\"APSReviews\", \"Exception throw: \" + e.toString());\n        }\n    });\n} catch (APSCloudException e) {\n    Log.e(\"APSReviews\", \"Exception thrown: \" + e.toString());\n}\n</code></pre>\n</div></div></div></div></div></div></div></div></div>"});